mapper.select:
select * from menu
where cafeName=#{cafeName}
order by menuName

select * from menu
where detailName=#{detailName} and cafeName=#{cafeName}
order by menuName


select *
from menu
where menuName like CONCAT('%', #{keyword}, '%') and cafeName=#{cafeName}

select count(*)
from menu
where menuName=#{menuName} and detailName=#{detailName} and cafeName=#{cafeName}
